mapper.select:
select userId, statId, chgerId, resDate, reservation.tId, starTime, endTime
from reservation
       left outer join time t
                       on reservation.tId = t.tId
where statId = #{statId}
  and chgerId = #{chgerId}
  and resDate = #{resDate}

select model
from carinfo
where manufac = #{manufac}
